var assoc    = require('ramda/src/assoc')
var converge = require('ramda/src/converge')
var identity = require('ramda/src/identity')
var uncurryN = require('ramda/src/uncurryN')

// assocWith :: String -> ({ k: v } -> a) -> { k: v } -> { k: v }
var assocWith = function (key, fn) { return converge(assoc(key), [fn, identity]); }

module.exports = uncurryN(3, assocWith)
