var compose  = require('ramda/src/compose')
var identity = require('ramda/src/identity')
var juxt     = require('ramda/src/juxt')
var mergeAll = require('ramda/src/mergeAll')
var uncurryN = require('ramda/src/uncurryN')

// combineAll :: [({ k: v }, ...) -> { k: v }] -> ({ k: v }, ...) -> { k: v }
var combineAll = function (fns) { return compose(mergeAll, juxt([ identity ].concat( fns ))); }

module.exports = uncurryN(2, combineAll)
