var converge = require('ramda/src/converge')
var identity = require('ramda/src/identity')
var uncurryN = require('ramda/src/uncurryN')

// combineWith :: (c -> b -> d) (a -> b) -> c -> d
var combineWith = function (mf, f) { return converge(mf, [ identity, f ]); }

module.exports = uncurryN(3, combineWith)
