var identity = require('ramda/src/identity')
var uncurryN = require('ramda/src/uncurryN')

var convergeP = require('./convergeP')

// combineWithP :: (c -> b -> d) (a -> Promise b) -> Promise c -> Promise d
var combineWithP = function (mf, f) { return convergeP(mf, [ identity, f ]); }

module.exports = uncurryN(3, combineWithP)
