var assocPath = require('ramda/src/assocPath')
var curry = require('ramda/src/curry')
var path = require('ramda/src/path')

// copyPath :: [String] -> [String] -> { k: v } -> { k: v }
var copyPath = function (fromPath, toPath, obj) { return assocPath(toPath, path(fromPath, obj), obj); }

module.exports = curry(copyPath)
